policy:
# Manage access to frontend secrets in UAT 
path "kv/data/uat/*" {
 capabilities = ["create", "read", "update", "delete", "list"]
}
 # Manage access to frontend secrets in Prod 
path "kv/data/prod/*" 
{
 capabilities = ["create", "read", "update", "delete", "list"]
}
# List data in datastorage 
path "kv/*" 
{
 capabilities = ["read", "list"]
}
